import { useState } from 'react'
import './index.css'

function Calculator() {
   const [count, setCount] = useState({
    number:0,
    sign:""
   })

   const optionCalculation = [
    "%","√","C","x",
    7,8,9,"/",
    4,5,6,"+",
    1,2,3,"-",
    "-",0,".", "="
   ]

   const button = optionCalculation.map(item => 
      <button key={item} value={item}>{item}</button>
   )

   const clickNumber = (e) => {
      e.preventDefault();
      setCount(...previousNumber => {
        return {...previousNumber, number:1}
      })
   }

   return (
       <>
            <div className="calculation-body">
                <h1>Calculator App</h1>
                <h1 className="bar-count">{count.number}</h1>
                {button}
            </div>
       </>
   )
}

export default Calculator;
