import { calculateNewValue } from '@testing-library/user-event/dist/utils'
import { useState } from 'react'
import './index.css'

function Calculator() {
   const [count, setCount] = useState({
    number:0,
    res:0,
    sign:""
   })

   const optionCalculation = [
    "%","√","C","x",
    7,8,9,"/",
    4,5,6,"+",
    1,2,3,"-",
    "@",0,".", "="
   ]

   const numClickHandler = (e) => {
    e.preventDefault();
    const valueNumber = e.target.innerHTML;

    if (count.number.length < 15) {
        setCount({
            ...count,
            number: 
              count.number === 0 && value === "0" ? "0":
              count.number % 1 === 0 ? Number(count.number + value):
              count.number + value,
            res : !count.sign ? 0 : count.sign, 
        })
    }
   }

   const button = optionCalculation.map((item, i) => 
      <button 
      key={i} 
      value={item} 
      onClick={
         item === "%" ? percentClickHandler :
         item === "√" ? squareRootClickHandler :
         item === "C" ? resetClickHandler:
         item === "x" || item === "/" || item === "-" || item === "+" ? signClickHandler :
         item === "=" ? equalsClickHandler : 
         item === "." ? commaClickHandler : numClickHandler     
      }>
      {item}
      </button>
   )

   return (
       <>
            <div className="calculation-body">
                <h1>Calculator App</h1>
                <h1 className="bar-count">{count.number}</h1>
                {button}
            </div>
       </>
   )
}

export default Calculator;
