import { useState } from 'react'
import './index.css'

const toLocaleString = (num) =>
  String(num).replace(/(?<!\..*)(\d)(?=(?:\d{3})+(?:\.|$))/g, "$1 ");

const removeSpaces = (num) => num.toString().replace(/\s/g, "");

function Calculator() {
   const [count, setCount] = useState({
    number:0,
    res:0,
    sign:""
   })

   const optionCalculation = [
    "%","√","C","x",
    7,8,9,"/",
    4,5,6,"+",
    1,2,3,"-",
    "@",0,".", "="
]

   const numClickHandler = (e) => {
    e.preventDefault();
    const valueNumber = e.target.innerHTML;

    if (count.number.length < 15) {
        setCount({
            ...count,
            number: 
              count.number === 0 && valueNumber === "0" ? "0":
              count.number % 1 === 0 ? Number(count.number + valueNumber):
              count.number + valueNumber,
            res : !count.sign ? 0 : count.sign, 
        })
    }
   }

   const numberChange = () => {
    setCount(previousState => {
        return {...previousState, number:0}
    })
   }

   const commaClickHandler = (e) => {
    e.preventDefault();
    const valueState = e.target.innerHTML;

    setCount({
        ...count,
        number: !count.number.toString().includes(".") ? count.number + valueState : count.number,  
    })
   }

   const signClickHandler = (e) => {
    e.preventDefault();
    const valueSign = e.target.innerHTML;

    setCount({
        ...count,
        sign : valueSign,
        res: !count.res && count.number ? count.number : count.res,
        number : 0,
    })
   }

   const equalsClickHandler = () => {
    if(count.sign && count.number) {
       const math = (a, b, sign) => 
       sign === "+" ? a+b:
       sign === "-" ? a-b:
       sign === "x" ? a*b:
       a/b;

       setCount({
        ...count,
        res: 
          count.number === "0" && count.sign === "/" ? "Can't divide with 0":
          math(Number(count.res), Number(count.number), count.sign), 
          sign: "",
          number: 0,
       });
    }
   }

   const percentClickHandler = () => {
    let number = count.number ? parseFloat(count.number) : 0;
    let res    = count.res ? parseFloat(count.res) : 0 

    setCount({
        ...count,
        number: (number /= Math.pow(100, 1)),
        res: (res /= Math.pow(100, 1)),
        sign: "",
    })
   }

   const squareRootClickHandler = () => {
    let number = count.number ? parseFloat(count.number) : 0;
    let res    = count.res ? parseFloat(count.res) : 0 

    setCount({
        ...count,
        number: (number /= Math.pow(100, 1)),
        res: (res /= Math.pow(100, 1)),
        sign: "",
    })
   }

   const resetClickHandler = () => {
    setCount({
        ...count, 
        sign:"",
        number:0,
        res:0,
    })
   }

   const button = optionCalculation.map((item, i) => 
      <button 
      key={i} 
      value={item} 
      onClick={() => 
         item === "%" ? percentClickHandler :
         item === "√" ? squareRootClickHandler :
         item === "C" ? resetClickHandler:
         item === "x" || item === "/" || item === "-" || item === "+" ? signClickHandler :
         item === "=" ? equalsClickHandler : 
         item === "." ? commaClickHandler : numClickHandler     
      }>
      {item}
      </button>
   )

   return (
       <>
            <div className="calculation-body">
                <h1>Calculator App</h1>
                <h1 className="bar-count">{count.number ? count.number : count.res}</h1>
                {button}
            </div>
       </>
   )
}

export default Calculator;
