import { useState } from 'react'
import './index.css'

function Calculator() {
   const [count, setCount] = useState({
     num : 0,
     sign : "",
     res : 0,
   })

   const optionCalculation = [
    "%","√","del","x",
    "7","8","9","/",
    "4","5","6","+",
    "1","2","3","-",
    "-","0",".", "="
   ]

   const button = optionCalculation.map((item) => 
      <button key={item}>{item}</button>
   )

   return (
            <div>
                <h1>Calculator App</h1>
                <input type="string" value={count.num}/>
                {button}
            </div>
   )
}

export default Calculator;
